def convert_filepath(original_path: str, commit_hash: str) -> str:
    # Extracting parts from the original path
    base_dir = os.path.dirname(os.path.dirname(original_path))
    relative_path = os.path.relpath(original_path, base_dir)
    
    # Replacing base directory with commit_history
    new_base_dir = os.path.join(base_dir, 'commit_history')
    new_relative_path = relative_path.replace('savchenko-physics.github.io.git', '')
    new_relative_path = new_relative_path.lstrip('\\/')  # Remove leading slashes
    
    # Changing file extension and inserting commit hash
    new_path = os.path.join(new_base_dir, new_relative_path)
    new_path = os.path.splitext(new_path)[0] + f'\{commit_hash}.html'
    
    return new_path

for commit in commit_list:
    # Fetch commit details      
    date_time, author, message = get_commit_details(commit)

    print(f"Processing commit: {commit}")
    # Checkout the specific commit
    subprocess.run(["git", "checkout", commit], stdout=subprocess.PIPE, stderr=subprocess.PIPE)

    # Get changed HTML files in this commit
    changed_files = subprocess.check_output(
        ["git", "diff-tree", "--no-commit-id", "--name-only", "-r", commit]
    ).decode().splitlines()

    for file in changed_files:
        if file.endswith('.html'):
            print (file)

            # Construct file paths
            source_path = os.path.join(local_repo_path, file)
            
            match = re.search(r'(\d+\.\d+\.\d+)', source_path)
            if match == None:
                continue

            problem_name = match.group(0)

            match = re.search(r'\\(en|[1-9]|1[0-4])/', source_path)

            language = match.group(1)

            # import time 

            print(language, problem_name)

            commit_output_dir = os.path.join(output_dir, language, problem_name)
            os.makedirs(commit_output_dir, exist_ok=True)

            # Define save path
            save_path = os.path.join(commit_output_dir, f"{commit}.html")



            print(source_path, save_path)

            # Copy the file to output directory
            try:
                shutil.copy(source_path, save_path)

                print(f"Saved commit {commit} to {save_path}")
            except Exception as e:
                print(e)

            csv_file_path = os.path.join(commit_output_dir, "commit_log.csv")
            
            with open(csv_file_path, mode='a', newline='') as csv_file:
                csv_writer = csv.writer(csv_file)
                if os.stat(csv_file_path).st_size == 0:
                    csv_writer.writerow(["Commit", "Date Time", "Contributor", "Description"])
                csv_writer.writerow([commit, date_time, author, message])


# Checkout back to the main branch
subprocess.run(["git", "checkout", "main"])

print("Processing completed.")
